mapper.select:
SELECT id,name,address,station,floor_plan_id AS floorPlanId
,building_date AS buildingDate,status FROM mansion
WHERE


        floor_plan_id = #{floorPlanId}


        building_date >= #{buildingDate}


        status = true

SELECT * FROM mansion


        name = #{name}


        AND station = #{station}

mapper.update:
update mansion

    name=#{name},
    address=#{address},
    station=#{station}

where id=#{id}
